package string;

public class arithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String add="addition";
		int a,b;
		int r=a+b;
		int s=a-b;
		switch(add) {
		case "addition":
			System.out.println("addition is"+r);
			break;
		case "subtraction":
			System.out.println("subtraction is"+s);
			break;
		}

	}

}
